public class JudgeRoutecircle {

    public static boolean judgeCircle(String moves) {
        int[] temp=new int[128];
        for(char i:moves.toCharArray()){
            temp[i]++;
        }
        if(temp['R']!=temp['L'] || temp['U']!=temp['D']){
            return false;
        }else{
            return true;
        }
    }

    public static void main(String[] args){
        String moves = "";
        judgeCircle(moves);
    }
}
